// Custom Scroll for Sidebar
$(function() {
	$('.users-container').slimScroll({
		color: '#d6dbe6',
		alwaysVisible: false,
		size: "5px",
		height: "100%",
		distance: '0px',
		railVisible: false,
		railColor: "#d6dbe6",
	});
});


// Custom Scroll 300
$(function() {
	$('.chatContainerScroll').slimScroll({
		height: "calc(100vh - 220px)",
		color: '#d6dbe6',
		alwaysVisible: false,
		size: "5px",
		distance: '0px',
		railVisible: false,
		railColor: "#d6dbe6",
	});
});


